const script = $input.first().json.script;
const jobId = $input.first().json.jobId;

// Detect job category from script
const lower = script.toLowerCase();
let category = 'general';

if (lower.includes('developer') || lower.includes('engineer') || lower.includes('software') || lower.includes('backend') || lower.includes('frontend') || lower.includes('fullstack')) {
  category = 'engineering';
} else if (lower.includes('designer') || lower.includes('ux') || lower.includes('ui') || lower.includes('graphic') || lower.includes('creative')) {
  category = 'design';
} else if (lower.includes('marketing') || lower.includes('seo') || lower.includes('content') || lower.includes('social media') || lower.includes('growth')) {
  category = 'marketing';
} else if (lower.includes('sales') || lower.includes('account executive') || lower.includes('business development')) {
  category = 'sales';
} else if (lower.includes('product manager') || lower.includes('product owner') || lower.includes('scrum')) {
  category = 'product';
} else if (lower.includes('data') || lower.includes('analyst') || lower.includes('analytics') || lower.includes('machine learning') || lower.includes('ai ')) {
  category = 'data';
} else if (lower.includes('hr') || lower.includes('recruiter') || lower.includes('talent') || lower.includes('people')) {
  category = 'hr';
} else if (lower.includes('finance') || lower.includes('accountant') || lower.includes('financial')) {
  category = 'finance';
} else if (lower.includes('support') || lower.includes('customer success') || lower.includes('community')) {
  category = 'support';
} else if (lower.includes('writer') || lower.includes('copywriter') || lower.includes('editor')) {
  category = 'writing';
}

// Category-specific search terms for Pexels
const categoryTerms = {
  engineering: ['coding on laptop', 'software developer typing', 'programming code screen', 'tech startup office', 'developer workspace'],
  design: ['creative studio workspace', 'designer working tablet', 'modern design office', 'creative team brainstorm', 'artistic workspace'],
  marketing: ['digital marketing team', 'social media analytics', 'marketing strategy meeting', 'creative campaign planning', 'brand presentation'],
  sales: ['business handshake deal', 'sales team meeting', 'professional presentation', 'corporate success celebration', 'business negotiation'],
  product: ['product roadmap planning', 'agile team standup', 'whiteboard strategy', 'team collaboration meeting', 'product launch'],
  data: ['data visualization charts', 'analytics dashboard screen', 'data science workspace', 'business intelligence', 'charts and graphs'],
  hr: ['job interview meeting', 'team onboarding welcome', 'diverse team collaboration', 'hiring process', 'workplace culture'],
  finance: ['financial analysis charts', 'business accounting', 'corporate finance meeting', 'investment planning', 'financial success'],
  support: ['customer service team', 'help desk support', 'customer success meeting', 'support team collaboration', 'happy customer'],
  writing: ['writer typing laptop', 'content creation workspace', 'creative writing coffee', 'journalist working', 'editorial office'],
  general: ['modern remote workspace', 'professional home office', 'successful career path', 'business growth success', 'happy professional working']
};

const terms = categoryTerms[category] || categoryTerms.general;

// Split into sentences
const sentences = script
  .split(/[.!?\n]+/)
  .map(s => s.trim())
  .filter(s => s.length > 0);

// Create scenes with category-themed search terms
const scenes = sentences.map((text, index) => {
  const searchTerms = [terms[index % terms.length]];
  return { text, searchTerms };
});

return [{
  json: {
    script: script,
    jobId: jobId,
    category: category,
    scenes: scenes,
    config: {
      voice: "af_heart",
      music: "hopeful",
      captionPosition: "bottom",
      orientation: "portrait",
      musicVolume: "low"
    }
  }
}];
